mapper.select:
select c.course_id,course_record_id,course_name,course_time_start,course_time_end,c.room_id,cr.room_name,stu_id,c.coach_id,score, comment_time,course_type, coach_name,DATE_FORMAT(course_time_start,'%Y-%m') as year_mouth, DATE_FORMAT(course_time_start,'%m-%d') as mouth_date, DATE_FORMAT(course_time_start,'%H:%i') as time_start_str, DATE_FORMAT(course_time_end,'%H:%i') as time_end_str FROM course c left join courserecord ON c.course_id=courserecord.course_id left join classroom cr on cr.room_id=c.room_id left join coach on c.coach_id=coach.coach_id where courserecord.stu_id= #{stuId}
AND course_time_start >= #{dateRange}
AND course_time_start <=#{dateRangeEnd}
group by year_mouth,c.course_id,course_name,course_time_start,course_time_end,c.room_id,room_name,c.coach_id,coach_name, course_type,course_capacity,course_surplus,course_record_id,score,comment_time order by course_time_start DESC

select c.course_id,course_name, course_time_start,course_time_end,c.room_id,cr.room_name,c.coach_id,coach.coach_name, DATE_FORMAT(course_time_start,'%Y-%m') as year_mouth, DATE_FORMAT(course_time_start,'%m-%d') as mouth_date, DATE_FORMAT(course_time_start,'%H:%i') as time_start_str, DATE_FORMAT(course_time_end,'%H:%i') as time_end_str from course c left join classroom cr on cr.room_id=c.room_id left join coach on c.coach_id=coach.coach_id where c.coach_id= #{coachId}
AND course_time_start >= #{dateRange}
AND course_time_start <=#{dateRangeEnd}
group by year_mouth,c.course_id,course_name, course_time_start,course_time_end,c.room_id,cr.room_name,c.coach_id,coach.coach_name order by course_time_start DESC

select course_name, avg(score) as avg_score from course c left join courserecord cr on c.course_id=cr.course_id where c.coach_id=#{coachId} and cr.course_id is not null
AND c.course_time_end < #{courseTimeStart}
group by course_name,cr.course_id
